mapper.select:
select qn.*,q.question_id,q.question_name,q.question_selection,q.question_type from
 questionnaire qn LEFT JOIN question q on qn.questionnaire_id = q.questionnaire_id
  where qn.questionnaire_id = #{questionnaireId,jdbcType=INTEGER}

select qn.*,q.question_id,q.question_name,q.question_selection,q.question_type,q.questionnaire_id questionnaire_id from questionnaire qn LEFT JOIN question q on qn.questionnaire_id = q.questionnaire_id
qn.questionnaire_name like "%" #{questionnaireCriteria.questionnaireName} "%"
order by q.questionnaire_id limit #{pageIndex},#{pageNum}
